class Solution:    # @param {string[]} strs a list of strings    # @return {string} encodes a list of strings to a single string    def encode(self, strs):        # Write your code here, LIST TO ONE        return "$".join(strs)    # @param {string} s a string    # @return {string[]} decodes a single string to a list of strings    def decode(self, s):        # Write your code here ONE TO LIST         array= []        start= 0        for counter, cha in enumerate(s):            if cha== "$":                array.append(s[start:counter])                start= counter+ 1        array.append(s[start:counter+1])        return array